        var video_markup_sub = '<a data-caption="'+ slide.title +'" data-video="'+slide.videoID+'" data-media-source="'+slide.source+'"><i class="fa fa-play" aria-hidden="true"></i>';
        video_markup_sub +=  '<img src="'+ slide.thumbnail +'" alt="" />';
